pragma solidity ^0.8.3;

import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableMapUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "./CryptoPageComment.sol";
import "./CryptoPageToken.sol";

contract PageCommentMinter is OwnableUpgradeable {
    using EnumerableMapUpgradeable for EnumerableMapUpgradeable.UintToAddressMap;
    using SafeMathUpgradeable for uint256;

    mapping(address => EnumerableMapUpgradeable.UintToAddressMap)
        private commentsByERC721;

    PageToken public token;
    address public treasury;

    function initialize(address _treasury, address _token)
        public
        payable
        initializer
    {
        treasury = _treasury;
        token = PageToken(_token);
        __Ownable_init();
    }

    function _exists(address _nft, uint256 _tokenId)
        private
        view
        returns (bool)
    {
        return commentsByERC721[_nft].contains(_tokenId);
    }

    function _get(address _nft, uint256 _tokenId)
        private
        view
        returns (PageComment)
    {
        return PageComment(commentsByERC721[_nft].get(_tokenId));
    }

    function _set(address _nft, uint256 _tokenId)
        private
        returns (PageComment)
    {
        PageComment comment = new PageComment();
        commentsByERC721[_nft].set(_tokenId, address(comment));
        return comment;
    }

    function isExists(address _nft, uint256 _tokenId)
        public
        view
        returns (bool)
    {
        return _exists(_nft, _tokenId);
    }

    function setTreasury(address _treasury) public onlyOwner {
        require(_treasury != address(0), "setTreasuryAddress: is zero address");
        treasury = _treasury;
    }

    function createComment(
        address _nft,
        uint256 _tokenId,
        address _author,
        string memory _text,
        bool _like
    ) public {
        uint256 amount = gasleft()
            .mul(tx.gasprice)
            .mul(token.getWBNBUSDTPrice())
            .mul(token.getUSDTPAGEPrice())
            .div(5)
            .mul(4);
        bool exists = _exists(_nft, _tokenId);
        if (exists) {
            _get(_nft, _tokenId).createComment(_author, _text, _like);
        } else {
            _set(_nft, _tokenId).createComment(_author, _text, _like);
        }
        uint256 accountAmount = amount.div(100).mul(45);
        uint256 treasuryAmount = amount.sub(accountAmount);
        IERC721Upgradeable nft = IERC721Upgradeable(_nft);

        token.mint(msg.sender, accountAmount);
        token.mint(nft.ownerOf(_tokenId), accountAmount);
        token.mint(treasury, treasuryAmount);
    }

    function getContract(address _nft, uint256 _tokenId)
        public
        view
        returns (PageComment)
    {
        require(_exists(_nft, _tokenId), "NFT contract does not exist");
        return _get(_nft, _tokenId);
    }

    function getTreasury() public view returns (address) {
        return treasury;
    }
}
","keccak256":"0x11132d290c15d32d1c2441b19cd62467adc5fbfd29b2f51368adc515d561c714","license":"MIT"},"contracts/CryptoPageToken.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.3;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "./interfaces/IUniswapV2Pair.sol";

contract PageToken is
    OwnableUpgradeable,
    AccessControlUpgradeable,
    ERC20Upgradeable
{
    using SafeMathUpgradeable for uint256;

    bytes32 private constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 private constant BURNER_ROLE = keccak256("BURNER_ROLE");

    address public usdtpagePair;
    address public wbnbusdtPair;

    function initialize(address _treasury) public initializer {
        __Ownable_init();
        __ERC20_init("Crypto.Page", "PAGE");
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _mint(_treasury, 5e25);
    }

    function setUSDTPAGEPair(address _usdtpagePair) public onlyOwner {
        usdtpagePair = _usdtpagePair;
    }

    function setWBNBUSDTPair(address _wbnbusdtPair) public onlyOwner {
        wbnbusdtPair = _wbnbusdtPair;
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burn(address to, uint256 amount) public onlyRole(BURNER_ROLE) {
        _burn(to, amount);
    }

    function getWBNBUSDTPrice() public view returns (uint256) {
        IUniswapV2Pair pair = IUniswapV2Pair(wbnbusdtPair);
        (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
        return reserve0.div(reserve1);
    }

    function getUSDTPAGEPrice() external view returns (uint256) {
        IUniswapV2Pair pair = IUniswapV2Pair(usdtpagePair);
        (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
        uint256 price = reserve1.div(reserve0);
        // Prevent price reduction less than 0.01 USDT
        if (price > 100) {
            price = 100;
        }
        return price;
    }
}
","keccak256":"0x22bf6a1d7d391c2aea598f3109f6ab50de6875ca996432d39f503a44465b21c7","license":"MIT"},"contracts/interfaces/IUniswapV2Pair.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.3;

interface IUniswapV2Pair {
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Transfer(address indexed from, address indexed to, uint256 value);

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);

    function PERMIT_TYPEHASH() external pure returns (bytes32);

    function nonces(address owner) external view returns (uint256);

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(
        address indexed sender,
        uint256 amount0,
        uint256 amount1,
        address indexed to
    );
    event Swap(
        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function price0CumulativeLast() external view returns (uint256);

    function price1CumulativeLast() external view returns (uint256);

    function kLast() external view returns (uint256);

    function mint(address to) external returns (uint256 liquidity);

    function burn(address to)
        external
        returns (uint256 amount0, uint256 amount1);

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;

    function skim(address to) external;

    function sync() external;

    function initialize(address, address) external;
}
