pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "./CryptoPageToken.sol";
import "./CryptoPageComment.sol";
import "./CryptoPageCommentMinter.sol";

contract PageNFT is ERC721("Page NFT", "PAGE-NFT"), ERC721URIStorage, Ownable {
    using Counters for Counters.Counter;
    using SafeMath for uint256;

    Counters.Counter private _tokenIdCounter;
    PageToken private token;
    PageCommentMinter private commentMinter;
    AggregatorV3Interface internal priceFeed;

    string public baseURL = "https://ipfs.io/ipfs/";
    address public treasury;
    uint256 public fee = 1000; // 100 is 1% || 10000 is 100%

    mapping(uint256 => address) private commentsById;
    mapping(uint256 => uint256) private pricesById;
    mapping(uint256 => address) private creatorById;

    constructor(
        address _treasury,
        address _token,
        address _commentMinter // address _priceFeed
    ) {
        treasury = _treasury;
        token = PageToken(_token);
        commentMinter = PageCommentMinter(_commentMinter);
    }

    function safeMint(string memory _tokenURI, bool _commentActive)
        public
        returns (uint256)
    {
        uint256 startGas = gasleft();
        uint256 tokenId = _mint(msg.sender, _tokenURI);
        if (_commentActive) {
            commentMinter.activateComments(address(this), tokenId);
        }
        uint256 amount = startGas - gasleft();
        amount.mul(uint256(token.getPrice()));
        uint256 treasuryAmount = amount.mul(fee).div(10000);
        uint256 senderAmount = amount.sub(treasuryAmount);
        pricesById[tokenId] = amount;
        token.mint(msg.sender, senderAmount);
        token.mint(treasury, treasuryAmount);
        return (tokenId);
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override {
        uint256 startGas = gasleft();
        // solhint-disable-next-line max-line-length
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "ERC721: transfer caller is not owner nor approved"
        );
        // require(ownerOf(tokenId) == msg.sender, "It's possible only for owner");
        // uint256 amount = startGas - gasleft();
        uint256 amount = startGas
            .sub(gasleft())
            .mul(uint256(token.getPrice()))
            .mul(tx.gasprice);
        uint256 accountAmount = amount.div(100).mul(30);
        uint256 treasuryAmount = amount.div(100).mul(10);
        token.mint(msg.sender, accountAmount);
        token.mint(creatorById[tokenId], accountAmount);
        token.mint(treasury, treasuryAmount);
        _transfer(from, to, tokenId);
        // _transferFrom(from, to, tokenId);
        // safeTransferFrom(from, to, tokenId);
        // uint256 amount = startGas - gasleft();
        // uint256 tokenPrice = uint256(token.getPrice());
        // uint256 amount = startGas.sub(gasleft()).mul(uint256(token.getPrice()));
        // uint256 accountAmount = amount.div(100).mul(30);
        // uint256 treasuryAmount = amount.div(100).mul(10);
        // token.mint(creatorById[tokenId], accountAmount);
        // token.mint(treasury, amount.div(100).mul(10));
    }

    function burn(uint256 _tokenId) public {
        // uint256 startGas = gasleft();
        require(
            ownerOf(_tokenId) == msg.sender,
            "It's possible only for owner"
        );

        // bool active = commentMinter.isActive(address(this), _tokenId);
        // uint256 burnPrice = startGas.sub(gasleft());
        PageComment commentContract = commentMinter.getContract(
            address(this),
            _tokenId
        );
        uint256 burnPrice = gasleft();
        // if (active) {
        PageComment.Comment[] memory comments = commentContract.getComments();
        burnPrice = burnPrice.sub(gasleft()).mul(tx.gasprice);
        for (uint256 i = 0; i < comments.length; i++) {
            PageComment.Comment memory comment = comments[i];
            burnPrice.add(comment.price.mul(2));
        }
        // }
        require(token.balanceOf(msg.sender) > burnPrice, "not enought balance");
        burnPrice.mul(uint256(token.getPrice()));
        token.burn(msg.sender, burnPrice);
        _burn(_tokenId);
    }

    function _mint(address owner, string memory _tokenURI)
        private
        returns (uint256)
    {
        uint256 tokenId = _tokenIdCounter.current();
        creatorById[tokenId] = owner;
        _safeMint(owner, tokenId);
        _setTokenURI(tokenId, _tokenURI);
        _tokenIdCounter.increment();

        return tokenId;
    }

    function _burn(uint256 tokenId)
        internal
        override(ERC721, ERC721URIStorage)
    {
        super._burn(tokenId);
    }

    function setTreasury(address _treasury) public onlyOwner {
        require(_treasury != address(0), "setTreasuryAddress: is zero address");
        treasury = _treasury;
    }

    function setFee(uint256 _percent) public onlyOwner {
        require(_percent >= 10, "setMintFee: minimum mint fee percent is 0.1%");
        require(
            _percent <= 3000,
            "setMintFee: maximum mint fee percent is 30%"
        );
        fee = _percent;
    }

    function tokenPrice(uint256 tokenId) public view returns (uint256) {
        require(tokenId <= _tokenIdCounter.current(), "No token with Id");
        return pricesById[tokenId];
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return string(abi.encodePacked(getBaseURL(), super.tokenURI(tokenId)));
    }

    function getBaseURL() public view returns (string memory) {
        return baseURL;
    }

    function getTreasury() public view returns (address) {
        return treasury;
    }

    function getFee() public view returns (uint256) {
        return fee;
    }
}
