","keccak256":"0x3778dc944f4a696335878bad8beca60f38b7c79b7a0bd8ddbeb618bd502a95ae","license":"MIT"},"contracts/CryptoPageComment.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract PageComment is Ownable {
    using EnumerableSet for EnumerableSet.UintSet;
    using Counters for Counters.Counter;

    struct Comment {
        uint256 id;
        address author;
        string text;
        bool like;
    }

    bool private active = true;
    uint256[] public commentsIds;

    mapping(uint256 => Comment) public commentsById;

    Counters.Counter private _totalLikes;
    Counters.Counter private _totalDislikes;

    event NewComment(uint256 id, address author, string text, bool like);

    modifier onlyActive() {
        require(active, "Comments not activated.");
        _;
    }

    constructor(address _owner) {
        transferOwnership(_owner);
    }

    function getActive() public view returns (bool) {
        return active;
    }

    function createComment(
        address author,
        string memory text,
        bool like
    ) public onlyActive {
        uint256 id = commentsIds.length;
        commentsIds.push(id);
        commentsById[id] = Comment(id, author, text, like);

        emit NewComment(id, author, text, like);

        _incrementStatistic(like);
    }

    function getCommentsIds() public view returns (uint256[] memory) {
        return commentsIds;
    }

    function getCommentsByIds(uint256[] memory _ids)
        public
        view
        returns (Comment[] memory)
    {
        require(_ids.length > 0, "_ids length must be more than zero");
        require(
            _ids.length <= commentsIds.length,
            "_ids length must be less or equal commentsIds"
        );

        Comment[] memory comments = new Comment[](_ids.length);
        for (uint256 i = 0; i < _ids.length; i++) {
            require(_ids[i] <= commentsIds.length, "No comment with this ID");
            Comment storage comment = commentsById[_ids[i]];
            comments[i] = comment;
        }
        return comments;
    }

    function getComments() public view returns (Comment[] memory) {
        return getCommentsByIds(commentsIds);
    }

    function getCommentById(uint256 id)
        public
        view
        returns (
            uint256,
            address,
            string memory,
            bool
        )
    {
        require(id <= commentsIds.length, "No comment with this ID");
        return (
            commentsById[id].id,
            commentsById[id].author,
            commentsById[id].text,
            commentsById[id].like
        );
    }

    function getStatistic()
        public
        view
        returns (
            uint256 total,
            uint256 likes,
            uint256 dislikes
        )
    {
        total = commentsIds.length;
        likes = _totalLikes.current();
        dislikes = _totalDislikes.current();
    }

    function toggleActive() public onlyOwner {
        if (active) {
            active = false;
        } else {
            active = true;
        }
    }

    function _incrementStatistic(bool like) private {
        if (like) {
            _totalLikes.increment();
        } else {
            _totalDislikes.increment();
        }
    }
}
","keccak256":"0xab9b79f8589c366e3b0bc7f09c556d596976c0c6eebe337d20eedde3a79868d9","license":"MIT"},"contracts/CryptoPageCommentMinter.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "./CryptoPageComment.sol";
import "./CryptoPageToken.sol";
import "./CryptoPageNFT.sol";

contract PageCommentMinter {
    using EnumerableMap for EnumerableMap.UintToAddressMap;
    mapping(address => EnumerableMap.UintToAddressMap) private commentsByERC721;

    PageToken public token;

    address public treasury;
    uint256 public amount = 3000000000000000000;
    uint256 public fee = 1000000000000000000;

    constructor(address _treasury, PageToken _token) {
        treasury = _treasury;
        token = _token;
    }

    function _exists(address _nft, uint256 _tokenId)
        public
        view
        returns (bool)
    {
        return commentsByERC721[_nft].contains(_tokenId);
    }

    function _get(address _nft, uint256 _tokenId)
        public
        view
        returns (PageComment)
    {
        return PageComment(commentsByERC721[_nft].get(_tokenId));
    }

    function _set(address _nft, uint256 _tokenId) public returns (PageComment) {
        IERC721 nft = IERC721(_nft);
        address owner = nft.ownerOf(_tokenId);
        PageComment comment = new PageComment(owner);
        commentsByERC721[_nft].set(_tokenId, address(comment));
        token.mint(owner, amount);
        token.mint(treasury, fee);
        return comment; // new PageComment();
    }

    function activated(address _nft, uint256 _tokenId)
        public
        view
        returns (bool)
    {
        return _exists(_nft, _tokenId);
    }

    function hasComments(address _nft, uint256 _tokenId)
        public
        view
        returns (bool)
    {
        return _exists(_nft, _tokenId);
    }

    function getContract(address _nft, uint256 _tokenId)
        public
        view
        returns (PageComment)
    {
        require(_exists(_nft, _tokenId), "NFT contract does not exist");
        return _get(_nft, _tokenId);
    }

    function activateComments(address _nft, uint256 _tokenId) public {
        _set(_nft, _tokenId);
    }

    function createComment(
        address _nft,
        uint256 _tokenId,
        address _author,
        string memory _text,
        bool _like
    ) public {
        bool exists = _exists(_nft, _tokenId);
        if (exists) {
            _get(_nft, _tokenId).createComment(_author, _text, _like);
        } else {
            _set(_nft, _tokenId).createComment(_author, _text, _like);
        }
    }
}
","keccak256":"0x23bbe833bdf81f3a1099051a11917f07e406cbcc096d03bac4ee6d31d11442a5","license":"MIT"},"contracts/CryptoPageNFT.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./CryptoPageToken.sol";
import "./CryptoPageComment.sol";
import "./CryptoPageCommentMinter.sol";

contract PageNFT is ERC721("Page NFT", "PAGE-NFT"), ERC721URIStorage, Ownable {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIdCounter;
    PageToken private token;
    PageCommentMinter private commentMinter;

    string public baseURL = "https://ipfs.io/ipfs/";
    address public treasury;
    uint256 public mintFee = 1000; // 100 is 1% || 10000 is 100%
    uint256 public burnFee = 0; // 100 is 1% || 10000 is 100%
    uint256 public amount = 9000000000000000000;
    uint256 public fee = 1000000000000000000;

    mapping(uint256 => address) private commentsById;
    mapping(uint256 => address) private creatorById;

    constructor(
        address _treasury,
        PageToken _token,
        PageCommentMinter _commentMinter
    ) {
        treasury = _treasury;
        token = _token;
        commentMinter = _commentMinter;
    }

    function safeMint(string memory _tokenURI, bool _commentActive)
        public
        returns (uint256)
    {
        uint256 tokenId = _mint(msg.sender, _tokenURI);
        if (_commentActive) {
            commentMinter.activateComments(address(this), tokenId);
        }
        token.mint(msg.sender, amount);
        token.mint(treasury, fee);
        return (tokenId);
    }

    function burn(uint256 _tokenId) public {
        require(
            ownerOf(_tokenId) == msg.sender,
            "It's possible only for owner"
        );
        token.burn(msg.sender, 10);
        _burn(_tokenId);
    }

    function _mint(address owner, string memory _tokenURI)
        private
        returns (uint256)
    {
        uint256 tokenId = _tokenIdCounter.current();
        creatorById[tokenId] = owner;
        _safeMint(owner, tokenId);
        _setTokenURI(tokenId, _tokenURI);
        _tokenIdCounter.increment();
        return tokenId;
    }

    function _burn(uint256 tokenId)
        internal
        override(ERC721, ERC721URIStorage)
    {
        super._burn(tokenId);
    }

    /*
    function burn(uint256 _tokenId) public onlyOwner {
        _burn(_tokenId);
    }
    */

    function setTreasury(address _treasury) public onlyOwner {
        require(_treasury != address(0), "setTreasuryAddress: is zero address");
        treasury = _treasury;
    }

    function setBurnFee(uint256 _percent) public onlyOwner {
        require(_percent >= 10, "setBurnFee: minimum burn fee percent is 0.1%");
        require(
            _percent <= 3000,
            "setBurnFee: maximum burn fee percent is 30%"
        );
        burnFee = _percent;
    }

    function setMintFee(uint256 _percent) public onlyOwner {
        require(_percent >= 10, "setMintFee: minimum mint fee percent is 0.1%");
        require(
            _percent <= 3000,
            "setMintFee: maximum mint fee percent is 30%"
        );
        mintFee = _percent;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return string(abi.encodePacked(getBaseURL(), super.tokenURI(tokenId)));
    }

    function getBaseURL() public view returns (string memory) {
        return baseURL;
    }

    function getTreasury() public view returns (address) {
        return treasury;
    }

    function getMintFee() public view returns (uint256) {
        return mintFee;
    }

    function getBurnFee() public view returns (uint256) {
        return burnFee;
    }
}
","keccak256":"0x0403ab9102572d1d0ce84f419df46ccc071490a24650518f9f117811a0147a10","license":"MIT"},"contracts/CryptoPageToken.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import "./Stakeable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract PageToken is
    ERC20("PageToken", "PAGE"),
    Stakeable,
    AccessControl,
    Ownable
{
    bytes32 private constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 private constant BURNER_ROLE = keccak256("BURNER_ROLE");

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burn(address to, uint256 amount) public onlyRole(BURNER_ROLE) {
        _burn(to, amount);
    }

    function stake(uint256 _amount) public {
        // Make sure staker actually is good for it
        require(
            _amount < balanceOf(msg.sender),
            "PageToken: Cannot stake more than you own"
        );

        _stake(_amount);
        // Burn the amount of tokens on the sender
        _burn(msg.sender, _amount);
    }

    /**
     * @notice withdrawStake is used to withdraw stakes from the account holder
     */
    function withdrawStake(uint256 amount, uint256 stakeIndex) public {
        uint256 amountToMint = _withdrawStake(amount, stakeIndex);
        // Return staked tokens to user
        _mint(msg.sender, amountToMint);
    }
}
","keccak256":"0x754de90f4a65bb7a3b0b8e621a8ebddf7969bb52a6c7469dd16122455f8309d1","license":"MIT"},"contracts/Stakeable.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

/**
 * @notice Stakeable is a contract who is ment to be inherited by other contract that wants Staking capabilities
 */
contract Stakeable {
    /**
     * @notice Constructor since this contract is not ment to be used without inheritance
     * push once to stakeholders for it to work proplerly
     */
    constructor() {
        // This push is needed so we avoid index 0 causing bug of index-1
        stakeholders.push();
    }

    /**
     * @notice
     * A stake struct is used to represent the way we store stakes,
     * A Stake will contain the users address, the amount staked and a timestamp,
     * Since which is when the stake was made
     */
    struct Stake {
        address user;
        uint256 amount;
        uint256 since;
        // This claimable field is new and used to tell how big of a reward is currently available
        uint256 claimable;
    }
    /**
     * @notice Stakeholder is a staker that has active stakes
     */
    struct Stakeholder {
        address user;
        Stake[] addressStakes;
    }
    /**
     * @notice
     * StakingSummary is a struct that is used to contain all stakes performed by a certain account
     */
    struct StakingSummary {
        uint256 totalAmount;
        Stake[] stakes;
    }

    /**
     * @notice
     *   This is a array where we store all Stakes that are performed on the Contract
     *   The stakes for each address are stored at a certain index, the index can be found using the stakes mapping
     */
    Stakeholder[] internal stakeholders;
    /**
     * @notice
     * stakes is used to keep track of the INDEX for the stakers in the stakes array
     */
    mapping(address => uint256) internal stakes;
    /**
     * @notice Staked event is triggered whenever a user stakes tokens, address is indexed to make it filterable
     */
    event Staked(
        address indexed user,
        uint256 amount,
        uint256 index,
        uint256 timestamp
    );

    /**
     * @notice
      rewardPerHour is 1000 because it is used to represent 0.001, since we only use integer numbers
      This will give users 0.1% reward for each staked token / H
     */
    uint256 internal rewardPerHour = 1000;

    /**
     * @notice _addStakeholder takes care of adding a stakeholder to the stakeholders array
     */
    function _addStakeholder(address staker) internal returns (uint256) {
        // Push a empty item to the Array to make space for our new stakeholder
        stakeholders.push();
        // Calculate the index of the last item in the array by Len-1
        uint256 userIndex = stakeholders.length - 1;
        // Assign the address to the new index
        stakeholders[userIndex].user = staker;
        // Add index to the stakeHolders
        stakes[staker] = userIndex;
        return userIndex;
    }

    /**
     * @notice
     * _Stake is used to make a stake for an sender. It will remove the amount staked from the stakers account and place those tokens inside a stake container
     * StakeID
     */
    function _stake(uint256 _amount) internal {
        // Simple check so that user does not stake 0
        require(_amount > 0, "Cannot stake nothing");

        // Mappings in solidity creates all values, but empty, so we can just check the address
        uint256 index = stakes[msg.sender];
        // block.timestamp = timestamp of the current block in seconds since the epoch
        uint256 timestamp = block.timestamp;
        // See if the staker already has a staked index or if its the first time
        if (index == 0) {
            // This stakeholder stakes for the first time
            // We need to add him to the stakeHolders and also map it into the Index of the stakes
            // The index returned will be the index of the stakeholder in the stakeholders array
            index = _addStakeholder(msg.sender);
        }

        // Use the index to push a new Stake
        // push a newly created Stake with the current block timestamp.
        stakeholders[index].addressStakes.push(
            Stake(msg.sender, _amount, timestamp, 0)
        );
        // Emit an event that the stake has occured
        emit Staked(msg.sender, _amount, index, timestamp);
    }

    /**
     * @notice
     * calculateStakeReward is used to calculate how much a user should be rewarded for their stakes
     * and the duration the stake has been active
     */
    function calculateStakeReward(Stake memory _currentStake)
        internal
        view
        returns (uint256)
    {
        // First calculate how long the stake has been active
        // Use current seconds since epoch - the seconds since epoch the stake was made
        // The output will be duration in SECONDS ,
        // We will reward the user 0.1% per Hour So thats 0.1% per 3600 seconds
        // the alghoritm is  seconds = block.timestamp - stake seconds (block.timestap - _stake.since)
        // hours = Seconds / 3600 (seconds /3600) 3600 is an variable in Solidity names hours
        // we then multiply each token by the hours staked , then divide by the rewardPerHour rate
        return
            (((block.timestamp - _currentStake.since) / 1 hours) *
                _currentStake.amount) / rewardPerHour;
    }

    /**
     * @notice
     * withdrawStake takes in an amount and a index of the stake and will remove tokens from that stake
     * Notice index of the stake is the users stake counter, starting at 0 for the first stake
     * Will return the amount to MINT onto the acount
     * Will also calculateStakeReward and reset timer
     */
    function _withdrawStake(uint256 amount, uint256 index)
        internal
        returns (uint256)
    {
        // Grab userIndex which is the index to use to grab the Stake[]
        uint256 userIndex = stakes[msg.sender];
        Stake memory currentStake = stakeholders[userIndex].addressStakes[
            index
        ];
        require(
            currentStake.amount >= amount,
            "Staking: Cannot withdraw more than you have staked"
        );

        // Calculate available Reward first before we start modifying data
        uint256 reward = calculateStakeReward(currentStake);
        // Remove by subtracting the money unstaked
        currentStake.amount = currentStake.amount - amount;
        // If stake is empty, 0, then remove it from the array of stakes
        if (currentStake.amount == 0) {
            delete stakeholders[userIndex].addressStakes[index];
        } else {
            // If not empty then replace the value of it
            stakeholders[userIndex].addressStakes[index].amount = currentStake
                .amount;
            // Reset timer of stake
            stakeholders[userIndex].addressStakes[index].since = block
                .timestamp;
        }

        return amount + reward;
    }

    /**
     * @notice
     * hasStake is used to check if a account has stakes and the total amount along with all the seperate stakes
     */
    function hasStake(address _staker)
        public
        view
        returns (StakingSummary memory)
    {
        // totalStakeAmount is used to count total staked amount of the address
        uint256 totalStakeAmount;
        // Keep a summary in memory since we need to calculate this
        StakingSummary memory summary = StakingSummary(
            0,
            stakeholders[stakes[_staker]].addressStakes
        );
        // Itterate all stakes and grab amount of stakes
        for (uint256 s = 0; s < summary.stakes.length; s += 1) {
            uint256 availableReward = calculateStakeReward(summary.stakes[s]);
            summary.stakes[s].claimable = availableReward;
            totalStakeAmount = totalStakeAmount + summary.stakes[s].amount;
        }
        // Assign calculate amount to summary
        summary.totalAmount = totalStakeAmount;
        return summary;
    }
}
